import React, { Component } from 'react';
import CenterItem from './CenterItem';
import './Centers.css';
import img1 from '../images/img-1.jpg'
import img2 from '../images/img-2.jpg'
import img3 from '../images/img-3.jpg'
import img4 from '../images/img-4.jpg'
import img5 from '../images/img-5.jpg'
import { useState, useEffect } from 'react';
import { FaAngleDown } from "react-icons/fa";
import axios from 'axios';

const elementStyle ={
    border:'solid',
    borderRadius:'10px',
    left:'10vh',
    height:'6vh',
    width:'60%',
    marginTop:'5vh',
    marginLeft:'5vh',
    marginBottom:'10vh'
  }
const sub ={
    border:'solid',
    borderRadius:'10px',
    width:"20%",
    height:'5vh',
    marginLeft:'1vh',
}  

const center_img = [img1, img2, img3, img4, img5, img1, img2, img3];
const center_name = []
const center_price = []
const center_label = []


function Centers({match}) {
    const [Centers, setCenters] = useState([{
        id: '',
        name: '',
        description: '',
        location: '',
        price: ''
      }])


    useEffect(async() => {
        try{
            const res = await axios.get("api/getAnyGym")
            const _Centers = await res.data.map((rowData)=>({
                id: rowData.id,
                name: rowData.name,
                description: rowData.description,
                location: rowData.location,
                price: rowData.price
            })
            )
        } catch(e){
            console.error(e.message)
        }
    }, [])
    //axios url로 불러오는 곳 실제 이런 식으로 불러오면 centers에 정보가 저장되므로 for문 돌려서 i.name처럼 값을 가져와서 저장만 해주면 된다.
    function Create_Center(){
        const results = []
        for (let i = 0; i < center_label.length; i++){
            results.push(<CenterItem className='create_center' src={center_img[i]} text= {center_name[i]} text2={center_price[i]} label={center_label[i]} path='/services' />);
            
        }
        let results__ = []
        let _results_ = []
        let cnt = 0;
        while(results.length != 0){
            results__.push(results.pop());
            cnt = cnt + 1;
            if (cnt == 3){
                _results_.push(<li display="inline-block">{results__}</li>)
                results__ = []
                cnt = 0;
            }
            else if(results.length == 1){
                _results_.push(<li display="inline-block">{results__}</li>)
            }
        }    
        return _results_;
    }

    return (
        <><div>
            <h1>우리동네 헬스장을 찾아보세요!</h1>
          </div>
        
        <div className='center__main__div'>
                    <div className='div_left'>  
                        <form action='/center_search' method='POST'>      
                        <input type="text" name='center_search' style={elementStyle}/>
                        <input type="submit" value="search" style={sub}></input>
                        </form>
                        <div className="App">
                            <div className="container">
                                <input id="dropdown" type="checkbox" />
                                <label className="dropdownLabel" for="dropdown">
                                <div>Location</div>
                                <FaAngleDown className="caretIcon" />
                                </label>
                                <div className="content">
                                <ul>
                                    <li><a href='/kwanakgu'>관악구</a></li>
                                    <li><a href='/dongdaemoon'>동대문</a></li>
                                    <li><a href='/shindang'>신당동</a></li>
                                </ul>
                                </div>
                            </div>
                            </div>
                    </div>
        </div>
                    <div className='create_center'>
                        <ul className='centers__items'>
                       
                            {Create_Center()}
                         
                        </ul>
                    </div>
            </>
    );
  }
  
  export default Centers;
