import React, { Component } from 'react';
import CenterItem from './CenterItem';
import './Centers.css';
import img1 from '../images/img-1.jpg'
import img2 from '../images/img-2.jpg'
import img3 from '../images/img-3.jpg'
import img4 from '../images/img-4.jpg'
import img5 from '../images/img-5.jpg'
import { useState } from 'react';

const center_img = [img1, img2, img3, img4, img5, img1, img2, img3];
const center_name = ['스포애니 관악점', '파크짐 동대문점', '엠비휘트니스', '을지휘트니스', '파고다헬스클럽', "석교헬스", "승기휘트니스", "윤표헬스클럽"];
const center_price = ['250,000KRW/MONTH', '120,000KRW/MONTH', '70,000KRW/MONTH', '140,000KRW/MONTH', '90,000KRW/MONTH', "1,200,000KRW/MONTH","200,000KRW/MONTH", "300,000KRW/MONTH"]
const center_label = ['관악구', '동대문', '신당동', '을지로', '수표로26길', "강남구", "장충동", "을지로"]
const elementStyle ={
    border:'solid',
    borderRadius:'10px',
    position:'relative',
    left:'10vh',
    height:'6vh',
    width:'70%',
    marginTop:'5vh',
    marginBottom:'10vh'
  }

function Centers() {
    function Create_Center(){
        const results = []
        for (let i = 0; i < center_label.length; i++){
           
            results.push(<CenterItem src={center_img[i]} text= {center_name[i]} text2={center_price[i]} label={center_label[i]} path='/services' />);
        }    
        return results;
    }

    return (
        <><div>
            <h1>우리동네 헬스장을 찾아보세요!</h1>
        </div>
        
        <div className='center__main__div'>
                    <div className='div_left'>  
                        <form action='POST'>      
                        <input type="text" name='center_search' style={elementStyle}/>
                        <input type="submit" name='submit'/>
                        </form>      

                    </div>
                    <div className='div_left'>
                        <ul className='centers__items'>
                            <li>
                            {Create_Center()}
                            </li>
                        </ul>
                    </div>
            </div>
            </>
    );
  }
  
  export default Centers;
