import React, { useState, useEffect } from 'react';
import '../../App.css';
import './Services.css';
import { Component } from "react"; 
import Form from "react-bootstrap/Form";
import Button from 'react-bootstrap/Button';
import  Axios from 'axios';
import {Link} from 'react-router-dom';

const Board = ({ 
    id, 
    title,
    content, 
    member_name, 
    registerDate,
    props
  }: { 
    id: number; 
    title: string;
    content:string; 
    member_name: string; 
    registerDate: string;
    props: any;
  }) => { 
    return ( 
        <div className="board_wrap">
        <div className="board_title">
            <strong>게시판</strong>
            <p>자유 게시판: 운동 정보를 공유하는 게시판입니다. </p>
        </div>
        <div className='board_view'>
            <div className="board_view_wrap">
                <div className="board_view">
            <div className="title">
                {title}
            </div>
            <div className="info">
                <dl>
                    <dt>번호</dt>
                    <dd>{id}</dd>
                </dl>
                <dl>
                    <dt>글쓴이</dt>
                    <dd>{member_name}</dd>
                </dl>
                <dl>
                    <dt>작성일</dt>
                    <dd>{registerDate}</dd>
                </dl>
            </div>
            <div className="cont">
                {content}
            </div>
            </div>
        <div className="bt_wrap">
            <Link to='/services'><Button variant="info" className='on'>목록으로 돌아가기</Button></Link>
        </div>       
        </div>
        </div>
    </div> 
    ); 
  };
  
class View extends Component{
    constructor(props: any) { 
        super(props); 
        this.state = { 
          boardList: [], 
        }; 
      }
      state = { 
        boardList: [],
      };
    getListNo = () => { 
        Axios.get("http://localhost:8080/api/getAnyPost/4", {}) 
          .then((res) => { 
            const { data } = res; 
            this.setState({ 
              boardList: data, 
            });
            this.props.renderComplete();
          }) 
          .catch((e) => { 
            console.error(e); 
          }); 
        };
    /** * @return {Component} Component */
    render() {
        // eslint-disable-next-line
        const { boardList }: { boardList: any } = this.state;
        return (
            boardList.map((v: any) => { 
                return (
                <Board
                  id={v.id}  
                  title={v.title}
                  member_name={v.memberName}
                  registerDate={v.createdDate}
                  key={v.id}
                  props={this}
                />
              ); 
             })
            );
        } 
    }
     
export default View;
