import React, { Component } from 'react';
import CenterItem from './CenterItem';
import './Centers.css';
import img1 from '../images/img-1.jpg'
import img2 from '../images/img-2.jpg'
import img3 from '../images/img-3.jpg'
import img4 from '../images/img-4.jpg'
import img5 from '../images/img-5.jpg'
import { useState, useEffect } from 'react';
import { FaAngleDown } from "react-icons/fa";
import axios from 'axios';

const elementStyle ={
    border:'solid',
    borderRadius:'10px',
    left:'10vh',
    height:'6vh',
    width:'60%',
    marginTop:'5vh',
    marginLeft:'5vh',
    marginBottom:'10vh'
  }
const sub ={
    border:'solid',
    borderRadius:'10px',
    width:"20%",
    height:'5vh',
    marginLeft:'1vh',
}  

let center_img = [img1, img2, img3, img4, img5, img1, img2, img3];
let center_name = []
let center_price = []
let center_location = []


function Centers({match}) {
    const [Centers, setCenters] = useState([{
        name: '',
        description: '',
        location: '',
        price: 0
      }])
      useEffect(async() => {
        try{
            const res = await axios.get("api/getAnyGym")
            const _Centers = await res.data.map((rowData)=>({
                name: rowData.name,
                description: rowData.description,
                location: rowData.location,
                price: rowData.price
            })
        )
        setCenters(Centers.concat(_Centers))
        }catch(e) {
            console.error(e.message)
        }
    }, [])


    for(let i = 1; i <= Centers.length(); i++){
        console(Centers[i].name)
    }
    // axios url로 불러오는 곳 실제 이런 식으로 불러오면 centers에 정보가 저장되므로 for문 돌려서 i.name처럼 값을 가져와서 저장만 해주면 된다.
