    //         results.push(<CenterItem className='create_center' src={center_img[i]} text= {center_name[i]} text2={center_price[i]} label={center_location[i]} path='/services' />);
            
    //     }
    //     let results__ = []
    //     let _results_ = []
    //     let cnt = 0;
    //     while(results.length != 0){
    //         results__.push(results.pop());
    //         cnt = cnt + 1;
    //         if (cnt == 3){
    //             _results_.push(<li display="inline-block">{results__}</li>)
    //             results__ = []
    //             cnt = 0;
    //         }
    //         else if(results.length == 1){
    //             _results_.push(<li display="inline-block">{results__}</li>)
    //         }
    //     }    
    //     return _results_;
    // }

    return (
        <><div>
            <h1>우리동네 헬스장을 찾아보세요!</h1>
          </div>
        
        <div className='center__main__div'>
                    <div className='div_left'>  
                        <form action='/center_search' method='POST'>      
                        <input type="text" name='center_search' style={elementStyle}/>
                        <input type="submit" value="search" style={sub}></input>
                        </form>
                        <div className="App">
                            <div className="container">
                                <input id="dropdown" type="checkbox" />
                                <label className="dropdownLabel" for="dropdown">
                                <div>Location</div>
                                <FaAngleDown className="caretIcon" />
                                </label>
                                <div className="content">
                                <ul>
                                    <li><a href='/kak'>관악구</a></li>
                                    <li><a href='/sb'>성북구</a></li>
                                    <li><a href='/kn'>강남구</a></li>
                                    <li><a href='/jg'>중구</a></li>
                                    <li><a href='/kb'>강북구</a></li>
                                </ul>
                                </div>
                            </div>
                            </div>
                    </div>
        </div>
                    <div className='create_center'>
                        <ul className='centers__items'>
                       
                       
                            {/* {Create_Center()} */}
                         
                        </ul>
                    </div>
            </>
    );
  }
  
  export default Centers;
