import React from 'react';
import '../../App.css';
import './Services.css';
import Table from 'react-bootstrap/Table'
import { Component } from 'react';
import Button from 'react-bootstrap/Button';
import {Link} from 'react-router-dom';
import Write from './Write';
import Axios from "axios";

const Board = ({ 
  id, 
  title, 
  member_name, 
  registerDate,
  props
}: { 
  id: number; 
  title: string; 
  member_name: string; 
  registerDate: string;
  props: any;
}) => { 
  return ( 
    <tr> 
      <td> 
        <input 
          type="checkbox"
          value={id}
          onChange={(e)=>{
            props.onCheckboxChange(e.currentTarget.checked,e.currentTarget.value);
        }}
        ></input> 
        </td> 
        <td>{title}</td> 
        <td>{member_name}</td> 
        <td>{registerDate}</td> 
    </tr> 
  ); 
};

interface IProps { 
  isComplete: boolean; 
  handleModify: any; 
  renderComplete: any; 
}
/** 
 * BoardList class 
 * @param {SS} e 
 */

class Services extends Component<IProps> {
  /** 
   * @param {SS} props
   */
   constructor(props: any) { 
    super(props); 
    this.state = { 
      boardList: [], 
      checkList: [], 
    }; 
  }

  state = { 
    boardList: [],
    checkList:[],
  }; 
  
  getList = () => { 
    Axios.get("http://localhost:8080/api/getAnyPost", {}) 
      .then((res) => { 
        const { data } = res; 
        this.setState({ 
          boardList: data, 
        });
        this.props.renderComplete();
      }) 
      .catch((e) => { 
        console.error(e); 
      }); 
    };
  handleDelete = () => {
    if (this.state.checkList.length == 0) {
        alert("삭제할 게시글을 선택하세요.");
        return;
    }

    let boardIdList = "";
  
    this.state.checkList.forEach((v: any) => {
        boardIdList += `'${v}',`;
    });
  
    Axios.post("http://localhost:8080/delete", {
        boardIdList: boardIdList.substring(0, boardIdList.length - 1),
    })
          .then(() => {
            this.getList();
          })
          .catch((e) => {
            console.error(e);
          });
  };
    /**
     * @param {boolean} checked 
     * @param {any} id 
     */
     onCheckboxChange = (checked: boolean, id: any) => { 
      const list: Array<string> = this.state.checkList.filter((v) => { 
        return v != id; 
      }); 

      if (checked) { 
        list.push(id); 
      } 
      this.setState({ 
        checkList: list, 
      }); 
    };

    componentDidMount() { 
      this.getList(); 
    }

    componentDidUpdate() { 
      if (!this.props.isComplete) { 
        this.getList(); 
      } 
    }

  /** 
   * @return {Component} Component 
   */ 
  render() {
    // eslint-disable-next-line
    const { boardList }: { boardList: any } = this.state;
    return (
      <div className="board_wrap">
        <div className="board_title">
            <strong>게시판</strong>
            <p>자유 게시판: 운동 정보를 공유하는 게시판입니다. </p>
        </div>
      <Table striped bordered hover className='board_list'> 
        <thead className='top'> 
          <tr>
            <th className='check'>선택</th>  
            <th className='title'>제목</th> 
            <th className='writer'>작성자</th> 
            <th className='date'>작성일</th> 
          </tr> 
        </thead> 
      <tbody> 
          {boardList.map((v: any) => { 
            return ( 
            <Board
              id={v.ID}  
              title={v.TITLE} 
              member_name={v.MEMBERNAME} 
              registerDate={v.createdDate}
              key={v.BOARD_ID}
              props={this}
            /> 
          ); 
         })}
        </tbody> 
      </Table>
      <div className="bt_wrap">
        <Button variant="info" className='on'><Link to='/write'>글쓰기</Link></Button> 
        <Button variant="secondary" onClick={() => { 
          this.props.handleModify(this.state.checkList); 
          }}
        >수정하기
        </Button> 
        <Button variant="danger" onClick={this.handleDelete}>삭제하기</Button>
      </div>
    </div>
    ); 
  } 
} 

export default Services;
