import React, { useState, useEffect } from 'react';
import '../../App.css';
import './Services.css';
import { Component } from "react"; 
import Form from "react-bootstrap/Form";
import Button from 'react-bootstrap/Button';
import  Axios from 'axios';
import {Link} from 'react-router-dom';
import Table from 'react-bootstrap/Table'

const Board = ({ 
    id, 
    title,
    content, 
    member_name, 
    registerDate,
    props
  }: { 
    id: number; 
    title: string;
    content:string; 
    member_name: string; 
    registerDate: string;
    props: any;
  }) => { 
    return ( 
        <tr> 
        <td>{id}</td>
        <td>{title}</td>
        <td>{content}</td> 
        <td>{member_name}</td> 
        <td>{registerDate}</td> 
    </tr>    
    ); 
  };
  
class View extends Component{
    constructor(props: any) { 
        super(props); 
        this.state = { 
          boardList: [], 
        }; 
      }
      state = { 
        boardList: [],
      };
    getListNo = () => { 
        Axios.get("http://localhost:8080/api/getAnyPost/4", {}) 
          .then((res) => { 
            const { data } = res; 
            this.setState({ 
              boardList: data, 
            });
            this.props.renderComplete();
          }) 
          .catch((e) => { 
            console.error(e); 
          }); 
        };
    /** * @return {Component} Component */
    render() {
        // eslint-disable-next-line
        const { boardList }: { boardList: any } = this.state;
        return (
          <div className="board_wrap">
            <div className="board_title">
                <strong>게시판</strong>
                <p>자유 게시판: 운동 정보를 공유하는 게시판입니다. </p>
            </div>
        <Table striped bordered hover className='board_list'> 
            <thead className='top'> 
            <tr>
                <th className='count'>번호</th>  
                <th className='title'>제목</th> 
                <th className='writer'>작성자</th> 
                <th className='date'>작성일</th>
            </tr> 
            </thead> 
        <tbody> 
          {boardList.map((v: any) => { 
            return (
            <Board
              id={v.id}  
              title={v.title} 
              member_name={v.memberName} 
              registerDate={v.createdDate}
              key={v.id}
              props={this}
            />
          ); 
         })}
        </tbody> 
      </Table>
            <div className="bt_wrap">
            <Link to='/services'><Button variant="info" className='on'>목록으로 돌아가기</Button></Link>
            </div>       
          </div>
            );
        } 
    }
     
export default View;
