import React from 'react';
import '../../App.css';
import './Services.css';
import { Component } from "react"; 
import Form from "react-bootstrap/Form";
import Button from 'react-bootstrap/Button';
import  Axios from 'axios';
import {Link} from 'react-router-dom';

interface IProps { 
    isModifyMode: boolean; 
    boardId: number; 
    handleCancel: any;
 }

/** 
 * Write class 
 * @param {SS} e 
 */
class Write extends Component{
   /**
     * @param {SS} props
     */
    constructor(props: any) { 
        super(props); 
        this.state = { 
            title: "",
            content: "", 
            isRendered: false, 
        }; 
    }

    state = {
        title: "", 
        content: "",
        member_name:"",
        random_password:"",
        isRendered:false,
    }; 

    write = () => { 
        Axios.post("http://localhost:8080/insert", { 
            title: this.state.title, 
            content: this.state.content,
            member_name:this.state.member_name,
            random_password:this.state.random_password,

        }) 
            .then((res) => { 
                this.setState({ 
                    title: "", 
                    content: "",
                    member_name:"",
                    random_password:"",
                }); 
                this.props.handleCancel();
            })
            .catch((e) => { 
                console.error(e); 
            });
    };

    update = () => { 
        Axios.post("http://localhost:8080/update", { 
            title: this.state.title, 
            content: this.state.content,
            member_name:this.state.member_name,
            random_password:this.state.random_password,
            id: this.props.boardId,
        }) 
        .then((res) => { 
            this.setState({
                title:"",
                content:"",
                member_name:"",
                random_password:"",
            });
            this.props.handleCancel();
        })
        .catch((e) => { 
            console.error(e); 
        }); 
    };

    detail = () => { 
        Axios.get(`http://localhost:8080/detail?id=${this.props.boardId}`) 
            .then((res) => { 
                if (res.data.length > 0) { 
                    this.setState({ 
                        title: res.data[0].TITLE, 
                        content: res.data[0].CONTENT,
                        member_name:res.data[0].MEMBER_NAME,
                        random_password:res.data[0].RANDOM_PASSWORD, 
                    }); 
                } 
            }) 
            .catch((e) => { 
                console.error(e); 
            }); 
        };
    
    handleChange = (e: any) => { 
        this.setState({ 
            [e.target.name] : e.target.value, 
        }); 
    };

    /** * * @param {any} prevProps */
    componentDidUpdate = (prevProps: any) => { 
        if (this.props.isModifyMode && this.props.boardId != prevProps.boardId) { 
            this.detail(); 
        } 
    };

    /** * @return {Component} Component */ 
    render() { 
        return ( 
        <div className="board_wrap">
            <div className="board_title">
                <strong>게시판</strong>
                <p>자유 게시판: 운동 정보를 공유하는 게시판입니다. </p>
            </div>
            <div className='board_write'>
            <Form>
                <div className='title'>
                <Form.Group className='mb-3'> 
                    <Form.Label className='title2'>제목</Form.Label> 
                    <Form.Control className='title3' 
                    type="text" 
                    name="title" 
                    value={this.state.title}  
                    placeholder="제목을 입력하세요"
                    onChange={this.handleChange} /> 
                </Form.Group>
                </div>
                <div className='info'>
                <Form.Group className='mb-3'> 
                    <Form.Label className='title2'>작성자</Form.Label> 
                    <Form.Control className='title3' 
                    type="text" 
                    name="member_name" 
                    value={this.state.member_name}  
                    placeholder="작성자 입력"
                    onChange={this.handleChange} /> 
                </Form.Group>
                <Form.Group className='mb-3'> 
                    <Form.Label className='title2'>비밀번호</Form.Label> 
                    <Form.Control className='title3' 
                    type="password" 
                    name="random_password" 
                    value={this.state.random_password}  
                    placeholder="비밀번호"
                    onChange={this.handleChange} /> 
                </Form.Group>
                </div>
                <div className='cont'>
                <Form.Group className='mb-3'> 
                    <Form.Label className='title2'></Form.Label> 
                    <Form.Control className='title3' 
                    as="textarea" 
                    name="content"
                    value={this.state.content}
                    placeholder="내용을 입력하세요"
                    onChange={this.handleChange} />  
                </Form.Group>
                </div>
            </Form>
            </div>
            <div className="bt_wrap">
                <Link to='/services'><Button variant="info" className='on' onClick={this.write}>작성완료</Button></Link>
                <Link to='/services'><Button variant="secondary" onClick={this.props.handleCancel}>취소</Button></Link>
            </div>       
        </div> 
            ); 
        } 
    } 
export default Write;
