import React, { useEffect } from 'react';
import { useLocation, useParams } from 'react-router-dom';



let saveId = []

for(let i = (Location.length-1) ; i < 0 ; i--){
    if(Location[i] == "/"){
        break;
    }
    saveId.unshift(Location[i])
}
console.log(saveId)

function Detail() {
    const Location = useLocation().pathname;
    
    return (
        <>
       <p1>{Location}</p1>
       </>
        );
    }
export default Detail;
