import React, { useState, useEffect } from 'react';
import '../../App.css';
import './Services.css';
import { Component } from "react"; 
import Form from "react-bootstrap/Form";
import Button from 'react-bootstrap/Button';
import  Axios from 'axios';
import qs from 'qs';
import {Link} from 'react-router-dom';

function useFetch(url, id) {
    const [data, setData] = useState(null);
    
    function fetchUrl() {
        Axios.get(`${url}?id=${id}`).then(response => {
            setData(response.data);
        });
    }
    useEffect(() => {
        if (id) {
            fetchUrl();
        } else {
            setData(null);
        }
    }, []);
    return [data];
}

const View = ({ location, history }) => {

    const query = qs.parse(location.search, {
        ignoreQueryPrefix: true
    });
    console.log(query);

    const [data, loading] = useFetch("/api/getAnyPost", query.id);
        return ( 
        <div className="board_wrap">
            <div className="board_title">
                <strong>게시판</strong>
                <p>자유 게시판: 운동 정보를 공유하는 게시판입니다. </p>
            </div>
            <div className='board_view'>
                <div className="board_view_wrap">
                    <div className="board_view">
                <div className="title">
                    {data.title}
                </div>
                <div className="info">
                    <dl>
                        <dt>번호</dt>
                        <dd>1</dd>
                    </dl>
                    <dl>
                        <dt>글쓴이</dt>
                        <dd>김이름</dd>
                    </dl>
                    <dl>
                        <dt>작성일</dt>
                        <dd>2021.1.16</dd>
                    </dl>
                    <dl>
                        <dt>조회</dt>
                        <dd>33</dd>
                    </dl>
                </div>
                <div className="cont">

                </div>
                </div>
            <div className="bt_wrap">
                <Link to='/services'><Button variant="info" className='on'>목록으로 돌아가기</Button></Link>
            </div>       
            </div>
        </div>
    </div>
            ); 
    } 
     
export default View;
